import React, {Component, Fragment} from "react"
import Navigation from "../components/Navigation"
import WireframeCard from "../components/WireframeCard"
import '../css/wireframespage.css'
import $ from 'jquery'
import Button from 'react-bootstrap/Button'
import Cookies from "universal-cookie"
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css"
import Loader from 'react-loader-spinner'
import { DOMAIN, BACKEND_ADDRESS } from '../consts'

export class WireframesPage extends Component{
    constructor(props) {
        super(props)
        if(this.props.history.location.state.wireframeList === undefined){
            window.location.href = DOMAIN
        }
        const cookie = new Cookies()
        this.state = {
            projectName : this.props.history.location.state.projectName,
            wireframeList : this.props.history.location.state.wireframeList,
            projectID: cookie.get('projectID'),
            accessToken: cookie.get('accessToken'),
            authType: cookie.get('authType'),
            selected:[],
            loaderVisible: false,
            timeMinutes: 0,
            timeSeconds: 0,
        }
        this.onChangeHandle = this.onChangeHandle.bind(this)
        this.toConvertPage = this.toConvertPage.bind(this)
        this.addToSelected = this.addToSelected.bind(this)
        this.removeSelected = this.removeSelected.bind(this)
        this.calcTimeEstimate = this.calcTimeEstimate.bind(this)
    }

    toConvertPage() {
        this.calcTimeEstimate(this.state.selected.length)
        this.setState({ 
            loaderVisible: true,
        })
        $.ajax({
            type: "POST",
            url: BACKEND_ADDRESS + '/generatePage',
            contentType: 'application/json',
            dataType: "json",
            async: true,
            data: JSON.stringify({
                'accessToken': this.state.accessToken,
                'projectID': this.state.projectID,
                'authType': this.state.authType,
                'pageList': this.state.selected
            }),
            success: function (data) {
                if(data.isSuccess){
                    window.location.href = BACKEND_ADDRESS + '/downloadFile?fileName='+ data.fileName
                }
                this.setState({ loaderVisible: false })
            }.bind(this),
            error: function (xhr, status, err) {
                console.log('error')
                this.setState({ loaderVisible: false })
            }.bind(this)
        })
    }

    addToSelected(name) {
        this.setState({
            selected:[...this.state.selected, name]
        })
    }

    removeSelected(name) {
        let array = Array.from(this.state.selected)
        let index = array.indexOf(name)
        if (index !== -1) {
            array.splice(index, 1)
            this.setState({selected: array})
        }
    }

    selectAll = () => {
        let array = []
        this.state.wireframeList.forEach(element => {
            array.push(element.name)
        })
        this.setState({ selected: array })
    }

    clearSelected = () => {
        this.setState({ selected: [] })
    }

    onChangeHandle(name) {
        let array = this.state.selected
        if(array.includes(name)){
            this.removeSelected(name)
        } else {
            this.addToSelected(name)
        }
    }

    calcTimeEstimate(numberOfScreens) {
        let total = numberOfScreens * 15
        let minutes = total/60>>0
        let seconds = total % 60
        this.setState({ 
            timeMinutes: minutes,
            timeSeconds: seconds,
        })
    }

    render() {
        return(
            <Fragment>
                <Navigation/>
                <div className='container-fluid container--margin-bottom'>
                    <div className='row'>
                        <div className='col-12 d-flex flex-column align-items-center'>
                            <h3 className='mt-5 mb-3'>Please select the wireframes that you wish to convert:</h3>
                        </div>
                    </div>
                    <div className='row'>
                    </div>
                    <div className='row'>
                        {this.state.wireframeList.map((item, index) => (
                            <div className={'col-6 col-sm-4 col-lg-3 col-xl-2 pt-4 pb-4 pr-3 pl-3'} key={index}>
                                <div 
                                    className='card-wrapper'
                                    onClick={() => this.onChangeHandle(item.name)}>
                                    <WireframeCard
                                        title={item.name}
                                        image = {item.imageURL}
                                        id = {item.id}
                                        selected={this.state.selected.includes(item.name)}
                                        onChange={() => this.onChangeHandle(item.name)}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
                <nav className="navbar fixed-bottom navbar-light bottom-actionbar">
                    <div>
                        <Button
                            className='mt-1'
                            onClick={() => this.selectAll()}>
                            Select all
                        </Button>
                        <Button
                            className='mt-1 ml-2'
                            onClick={() => this.clearSelected()}>
                            Unselect all
                        </Button>
                    </div>
                    <span className="bottom-actionbar__selected-text">Currently selected: {this.state.selected.length}</span>
                    <Button 
                        className='bottom-actionbar__button-convert mt-1' 
                        onClick={() => this.toConvertPage()}
                        disabled={this.state.selected.length === 0}>
                        Convert to code
                    </Button>
                </nav>
                {this.state.loaderVisible &&
                    <div className="d-flex justify-content-center align-items-center loader-background">
                        <div className="d-flex align-items-center flex-column loader-wrapper">
                            <h4 className="mb-4">We are generating your App template</h4>
                            <Loader
                                type="Watch"
                                color="#005EB8"
                                width={50}
                                height={50}/>
                            <p className="lead mt-4">This will take approximatelly {this.state.timeMinutes} minute(s) and {this.state.timeSeconds} seconds.</p>
                        </div>
                    </div>
                }
            </Fragment>
        )
    }
}

export default WireframesPage
